        GROUPID=$( grep &#39;groupId&#39; $POMPROPS | cut -d&#39;=&#39; -f2 | tr -d &#39;\r&#39; );
        VERSION=$( grep &#39;version&#39; $POMPROPS | cut -d&#39;=&#39; -f2 | tr -d &#39;\r&#39; );
        if [[ -f $POMPROPS &amp;&amp; -f $POMXML ]]; then
