import java.util.Arrays;

public class CountSwaps{
	static int countSwaps( char[] original, char[] sorted){
		int swaps = 0;
		for(int i = 0; i&lt;original.length; i++){
			if(original[i] != sorted[i]){
				for(int j = i+1; j&lt;original.length; j++){
					if(original[i] == sorted[j]){
