    procedure getEmployeeById(
            id_in IN EMPLOYEE.ID%type,
            e_disp OUT SYS_REFCURSOR
        ) IS
            hasEmployee number;
        BEGIN
            hasEmployee := 0;
            SELECT count(*) into hasEmployee from EMPLOYEE where ID = id_in;
            IF hasEmployee &lt;&gt; 0 THEN --here &lt;&gt; means !=
