		// create avro writer
		DatumWriter&lt;Account&gt; datumWriter = new ReflectDatumWriter&lt;&gt;(schema);
		DataFileWriter&lt;Account&gt; dataFileWriter = new DataFileWriter&lt;&gt;(datumWriter);
