import kotlin.time.*

fun main() {
    val millis = 260100
    val duration = millis.toDuration(DurationUnit.MILLISECONDS)
    val timeString = 
    	duration.toComponents { minutes, seconds, _ -&gt; 
        	String.format(&quot;%02d:%02d&quot;, minutes, seconds)
    	}
    println(timeString)  // prints 04:20
}
```

