  x = MouseInfo.getPointerInfo().getLocation().x;
  y = MouseInfo.getPointerInfo().getLocation().y;
 }
 surface.setLocation(x, y);

 fill(255);
 ellipse(width/2, height/2, 100, 100);
}

void mousePressed() {
 moving=true;
}

void mouseReleased() {
 moving=false;
}
