function solution(arr) {
  const r1 = arr.map((n, i) =&gt; ({from: i - n, to: n + i})).sort((a, b) =&gt; a.from - b.from)
  const r2 = r1.slice().sort((a, b) =&gt; a.to - b.to)
  let i = 0, j = 0
  let overlaps = 0
  let pairs = 0

  while (i &lt; r1.length || j &lt; r2.length) {
    if (i &lt; r1.length &amp;&amp; r1[i].from &lt;= r2[j].to) {
      pairs += overlaps
      if (pairs &gt; 10000000) {
        return -1
      }
      overlaps++
      i++
    } else {
      overlaps--
      j++
    }
  }
  return pairs
}
