public class HTree {

    private void display() {
        JFrame f = new JFrame(&quot;HTree&quot;);
        f.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        f.add(new Tree());
        f.pack();
        f.setLocationRelativeTo(null);
        f.setVisible(true);
