```java
public static List&lt;Integer&gt; getEmptyCellPairs(int[][] grid) {
    List&lt;Integer&gt; result = new ArrayList&lt;&gt;();
    for (int i = 0; i &lt; grid.length; i++) {
        for (int j = 0; j &lt; grid[i].length; j++) {
            if (0 == grid[i][j]) {
                result.add(i);
                result.add(j);
            }
        }
    }
    return result;
}


