	public class PanelTable2 extends JPanel {
	
		public PanelTable2() {
	
			setLayout(new BoxLayout(this, BoxLayout.Y_AXIS));
			setPreferredSize(new Dimension(400, 150));
			add(getRow());
		}
	
		Component getRow() {
	
			int[] percent =  {10,40,50};
	
			JPanel row = new JPanel();
			GridBagLayout layout = new GridBagLayout();
			row.setLayout(layout);
	
			GridBagConstraints gbc = new GridBagConstraints();
			gbc.fill = GridBagConstraints.HORIZONTAL;
			gbc.anchor = GridBagConstraints.CENTER;
	
			for (int i= 0; i &lt; percent.length; i++) {
				gbc.gridy = 0 ; gbc.gridx = i;
				gbc.weightx = percent[i];
				Component component =  getCell();
				row.add(component, gbc);
			}
			return row;
		}
	
		Component getCell() {
	
			JTextArea ta = new JTextArea(4,11);
			ta.setBorder(BorderFactory.createLineBorder(Color.BLACK));
			ta.setFont(new Font(&quot;Bmitra&quot;, Font.BOLD, 15));
			return ta;
		}
	
		public static void main(String[] args) throws IOException{
	
			JFrame window = new JFrame();
			window.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
			window.add(new PanelTable2());
			window.pack();
			window.setVisible(true);
		}
	}

