    #include &lt;cstdint&gt;
    #include &lt;vector&gt;
    #include &lt;iostream&gt;
    
    struct TreeElement {
        uint32_t id;
        uint32_t idx;
        uint32_t parent;
    
        [[nodiscard]]
        bool operator==(const TreeElement&amp; other) const noexcept {
            return id == other.id &amp;&amp; idx == other.idx &amp;&amp; parent == other.parent;
        }
    };
    
    void elem_swap(TreeElement&amp; first, TreeElement&amp; second) {
        TreeElement tmp = first;
        first           = second;
        second          = tmp;
    }
    
    // returns number of processed elements
    uint32_t emplace_children(TreeElement* elements, size_t count, uint32_t parent) {
        if(!count) {
            return 0;
        }
    
        uint32_t i = 0;
        for(; i &lt; count; ++i) {
            uint32_t     lowestChildIdx = 0;
            bool         found          = false;
            for(uint32_t j              = i; j &lt; count; ++j) {
                if(elements[j].parent == parent) {
                    if(!found || elements[j].idx &lt;= elements[lowestChildIdx].idx) {
                        lowestChildIdx = j;
                    }
                    found = true;
                }
            }
    
            if(!found) {
                return i;
            }
    
            elem_swap(elements[i], elements[lowestChildIdx]);
    
            i += emplace_children(elements + i + 1, count - i - 1, elements[i].id);
        }
    
        return i;
    }
    
