    import java.util.*;
    class Solution{
        static boolean sol(int a, int b, int x, int y){
            if( a == x &amp;&amp; b == y)
                return true;
            else if(a &gt; x || b &gt; y)
                return false;
            else{
                if(sol(a+b, a, x, y)) return true;
                if(sol(a, a+b, x, y)) return true;
            }
