	}
	
	public void start() {
		
		char player = &#39;X&#39;;
		Random r = new Random();
		outer: while (moveCount &lt; MAX_MOVES) {
			commonGroupings.entrySet().forEach(System.out::println);
			
			System.out.println();
			int row = r.nextInt(3) * 2;
			int col = r.nextInt(3) * 2;
			System.out.println(&quot;Move: &quot; + row + &quot;, &quot; + col);
			player = player == &#39;X&#39; ? &#39;O&#39; : &#39;X&#39;;
