	public char recordMove(int row, int col, char c) {
		moveCount++;
		
		for (TriGroup tri : commonGroupings.get(row + &quot;&quot; + col)) {
			if (tri.group.length() &gt; 2) {
				// just ignore the row/col and try the next
				continue;
			}
			
			// update group
			tri.group += c;
			if (tri.group.equals(c + &quot;&quot; + c + &quot;&quot; + c)) {
