    public class gliding_the_mouse {

        public void mouseGlide(int x1, int y1, int x2, int y2, int t, int n) {
            try {
                Robot r = new Robot();
                double dx = (x2 - x1) / ((double) n);
                double dy = (y2 - y1) / ((double) n);
                double dt = t / ((double) n);
                for (int step = 1; step &lt;= n; step++) {
