    Feign.builder().setInterceptor(requestTemplate -&gt; {
            Map&lt;String, Collection&lt;String&gt;&gt; map = new HashMap&lt;&gt;(requestTemplate.headers());
            map.put(&quot;Accept&quot;, Collections.singleton(&quot;application/json&quot;));
            requestTemplate.headers(null);
