import org.springframework.security.web.util.matcher.RequestMatcher;
import org.springframework.web.filter.OncePerRequestFilter;

import hype.security.authentication.apikey.ApiKeyAuthenticationFilter;
import hype.security.authentication.jwt.JwtBearerTokenAuthenticationFilter;

public class CompositeAuthenticationFilter extends OncePerRequestFilter {

    private ApiKeyAuthenticationFilter apiKeyAuthenticationFilter;
    private JwtBearerTokenAuthenticationFilter jwtBearerTokenAuthenticationFilter;

    @Value(&quot;${private-api-key-uri}&quot;)
    private String privateApiUri;


    @Value(&quot;${private-api-uri}&quot;)
    private String privateApiKeyUri;

    // Add this property for permitted URLs
    private final OrRequestMatcher permittedRequestMatcher;

    // Update the constructor to accept the permitted URLs patterns
    public CompositeAuthenticationFilter(ApiKeyAuthenticationFilter apiKeyAuthenticationFilter,
                                         JwtBearerTokenAuthenticationFilter jwtBearerTokenAuthenticationFilter,
                                         String[] permittedUrlPatterns) {
        this.apiKeyAuthenticationFilter = apiKeyAuthenticationFilter;
        this.jwtBearerTokenAuthenticationFilter = jwtBearerTokenAuthenticationFilter;
        this.permittedRequestMatcher = createOrRequestMatcher(permittedUrlPatterns);
    }

    // Add this method to create an OrRequestMatcher for the permitted URLs
    private OrRequestMatcher createOrRequestMatcher(String[] patterns) {
        List&lt;RequestMatcher&gt; matchers = Arrays.stream(patterns)
                .map(AntPathRequestMatcher::new)
                .collect(Collectors.toList());
        return new OrRequestMatcher(matchers);
    }

    @Override
    protected boolean shouldNotFilter(HttpServletRequest request) {
        return permittedRequestMatcher.matches(request);
    }

    @Override
    protected void doFilterInternal(HttpServletRequest request, HttpServletResponse response, FilterChain filterChain)
            throws ServletException, IOException {
        Authentication apiKeyAuthResult = apiKeyAuthenticationFilter.attemptAuthentication(request, response);
        if (apiKeyAuthResult != null &amp;&amp; apiKeyAuthResult.isAuthenticated()) {
            SecurityContextHolder.getContext().setAuthentication(apiKeyAuthResult);
            apiKeyAuthenticationFilter.successfulAuthentication(request, response, filterChain, apiKeyAuthResult);
            filterChain.doFilter(request, response);
        } else {
            Authentication bearerTokenAuthResult = jwtBearerTokenAuthenticationFilter.attemptAuthentication(request,
                    response);
