            Vehicle[] vehicles = {
                new Plane(),
                new Car(),
                new Car(),
                new Car(),
                new Boat(),
                new Boat()
            };
		
            VehicleManager manager = new VehicleManager();
                for(Vehicle vehicle : vehicles) {
                    manager.add(vehicle);
                }
		
                manager.printAllVehicles();
        }
    }

