    public int f(int x, int g_z, int g_w, int h_x, int h_y) {
      if (x == 0) {
        return 0;
      } else {
        return g_z + x + g(g_w - 1, h_x, h_y);
      }
    }

    public int g(int w, int h_x, int h_y) {
      int z = h_y + 1;
