                x = scanner.nextInt();
            } catch(InputMismatchException exc) {
                  //error message
            } catch(IllegalStateException exc) {
                  //error message
            } catch(NoSuchElementException exc) {
                 //error message
            }
        }
        return x;
    }

    public static void main(String[] args) {
