	env.put( Context.INITIAL_CONTEXT_FACTORY, &quot;com.sun.jndi.ldap.LdapCtxFactory&quot; );
	env.put( Context.PROVIDER_URL, ( encryption == SSL ? &quot;ldaps://&quot; : &quot;ldap://&quot; ) + host + &quot;:&quot; + port );
	if ( encryption == SSL ) {
