	ctx = new InitialLdapContext( env, null );
	if ( encryption != START_TLS )
		tls = null;
	else {
		tls = (StartTlsResponse) ctx.extendedOperation( new StartTlsRequest() );
		tls.setHostnameVerifier( hostnameVerifier );
