		private Iterator&lt;T&gt; chunk;
		private Long count;
		private long index = 0;
		private long chunkSize = 100;
		
		public ChunkIterator(Long count, long chunkSize) {
			super();
			this.count = count;
			this.chunkSize = chunkSize;
