			return this;
		}

		@Override
		protected T computeNext() {
			if (count == 0) return endOfData();
			if (chunk != null &amp;&amp; chunk.hasNext() == false &amp;&amp; index &gt;= count) 
				return endOfData();
			if (chunk == null || chunk.hasNext() == false) {
				chunk = getChunk(index, chunkSize);
				index += chunkSize;
			}
			if (chunk == null || chunk.hasNext() == false) 
				return endOfData();
