            for (int x : arr) {
                // we assume input is unsigned bytes so we should not need to mask
                //  with 0xFF here
                int unsigned_byte = x &amp; 0xFF;
                sum += unsigned_byte;
                sum %= 65536;
            }
            return sum;
        }
    }

