       for (int i = activeEffects.size - 1; i &gt;= 0; i--) {

            final PooledEffect effect = activeEffects.get(i);
            effect.update(Gdx.graphics.getDeltaTime());
            effect.draw(spriteBatch);

            if (effect.isComplete()) {
                effect.reset();
                effect.free();
                activeEffects.removeIndex(i);

            }
        }
