    public class MyController {
    	Flux&lt;String&gt; stringFlux = Flux.fromIterable(List.of(&quot;CM&quot;, &quot;Abdullah&quot;, &quot;Khan&quot;));
    	@GetMapping(produces = MediaType.TEXT_EVENT_STREAM_VALUE)
    	Flux&lt;String&gt; getItems() {
