	String NOT_LONG_ENOUGH = &quot;PabcO5j&quot;;
	String NOT_NUM_AT_6 = &quot;PabcOTTggj&quot;;
	List&lt;String&gt; pwds = Arrays.asList( GOOD_PWD, NOT_LONG_ENOUGH, NOT_NUM_AT_6 );

