class Solution {
    public int myAtoi(String s) {
        int length = s.length();
        boolean pos = true;
        int i = 0, num = 0;
        
        if(s.length() == 0) {
            return 0;
        }
        
        while(s.charAt(i) == &#39; &#39;) {
            i++;
        }
        
        if(s.charAt(i) == &#39;-&#39;) {
            pos = false;
            i++;
        }
        else if(s.charAt(i) == &#39;+&#39;) {
            pos = true;
            i++;
        }
        
        while(i &lt; length) {
            if(Character.isDigit(s.charAt(i))) {
                num = (num * 10) + Character.getNumericValue(s.charAt(i));
            } 
            i++;
        }
        
        if(!pos) {
            num *= -1;
        }
        
        if(num &lt; Integer.MIN_VALUE) {
            num = Integer.MIN_VALUE;
        }
        else if(num &gt; Integer.MAX_VALUE) {
            num = Integer.MAX_VALUE;
        }
        
        return num;
    }
}
