     */
    public void check(@IdRes int id) {
        // don&#39;t even bother
        if (id != -1 &amp;&amp; (id == checkedButtonID)) {
            return;
        }
        if (checkedButtonID != -1) {
            setCheckedStateForView(checkedButtonID, false);
        }
        if (id != -1) {
            setCheckedStateForView(id, true);
        }
        setCheckedId(id);
    }

    /**
