    }
    
    template &lt;size_t N&gt;
    void sorting( int ( &amp;a )[N] )
    {
    	for (size_t i = 0; i &lt; N; i++)
    	{
    		size_t min = i;
    
    		for (size_t j = i + 1; j &lt; N; j++)
