   {
      super(value);
   }
   
   public Integer convert(GenericValue&lt;?&gt; inputType)
   {
   
      if (inputType instanceof IntegerValue)
      {
      
         IntegerValue temp = ((IntegerValue) inputType);
      
         return (temp.getValue());
      
      }
      
      else if (inputType instanceof StringValue)
