Before:

      task pathingJar(type: Jar) {
        dependsOn configurations.developmentOnly
        archiveAppendix = &#39;pathing&#39;
    
        doFirst {
            manifest {
                attributes &quot;Class-Path&quot;: configurations.developmentOnly.files.collect {
