After:

    task pathingJar(type: Jar) {
        dependsOn configurations.runtimeClasspath
        archiveAppendix = &#39;pathing&#39;
    
        doFirst {
            manifest {
                attributes &quot;Class-Path&quot;: configurations.runtimeClasspath.files.collect {
                    it.toURI().toString().replaceFirst(/file:\/+/, &#39;/&#39;)
                }.join(&#39; &#39;)
            }
        }
