                    arrayC[cursorC] = aValue;
                    cursorA++;
    
                } else {
                    // Add from array B
                    bValue = arrayB[cursorB];
                    arrayC[cursorC] = bValue;
                    cursorB++;
                }
            }
    
            return arrayC;
