		//create a query 
		Query&lt;Customer&gt; theQuery = currentSession.createQuery(&quot;from Customer&quot;, Customer.class); 
		
		//execute query and result list 
		List&lt;Customer&gt; customers = theQuery.getResultList(); 
		
		//return the results 
		return customers;
	}

}

