    public class TreeNode&lt;T&gt; {
    	
    	T data;
    	TreeNode&lt;T&gt; left;
    	TreeNode&lt;T&gt; right;
    	
    	public TreeNode(T data) {
    		this.data = data;;
    	}
    	
