&lt;h3&gt;D - MyQueue class : A simple Queue Implementation&lt;/h3&gt;

    public class MyQueue&lt;T&gt; {
    	
    	private static class Node&lt;T&gt; {
    		
    		T data;
    		Node next;
    		
    		public Node(T data) {
    			this(data, null);
    		}
    		
    		public Node(T data, Node&lt;T&gt; next) {
    			this.data = data;
    			this.next = next;
    		}
    		
    	}
    	
    	private Node head;
    	private Node tail;
    	private int size;
    	
    	public MyQueue() {
    		head = null;
    		tail = null;
    	}
    	
    	public int size() {
    		return size;
    	}
    	
    	public void enqueue(T data) {
    		if(data == null)
    			return;
    		
    		if(head == null)
    			head = tail = new Node(data);
    		else {
    			tail.next = new Node(data);
    			tail = tail.next;
    		}
    		
    		size++;
    	}
    	
    	public T dequeue() {
    		
    		if(tail != null) {
    			T temp = (T) head.data;
    			head = head.next;
    			
    			size--;
    			
    			return temp;
    		}
    		
    		return null;
    	}
    	
    	public boolean isEmpty() {
    		return size == 0;
    	}
    	
    	public void printQueue() {
    		System.out.println(&quot;Queue: &quot;);
    		if(head == null)
    			return;
    		else {
    			Node&lt;T&gt; temp = head;
    			while(temp != null) {
    				System.out.printf(&quot;%s &quot;, temp.data);
    				temp = temp.next;
    			}
    		}
    		System.out.printf(&quot;%n&quot;);
