new ProcessBuilder(
  &quot;powershell.exe&quot;, 
  &quot;Start-Process powershell.exe &#39;-NoExit \&quot;[Console]::Title = &#39;&#39;servicemix&#39;&#39;; Get-ChildItem\&quot;&#39;&quot;
