            public SplashPane() {
                setLayout(new BorderLayout(0, 4));
                setBorder(new EmptyBorder(10, 10, 10, 10));

                JPanel panel = new JPanel(new GridBagLayout());
                panel.setBorder(new CompoundBorder(
                        new javax.swing.border.EtchedBorder(),
