        private String convert(AnnotatedMethod method, String defaultName) {
            
            Class&lt;?&gt; clazz = method.getDeclaringClass();
            List&lt;Field&gt; flds = FieldUtils.getAllFieldsList(clazz);
            for (Field fld : flds) {
                if (fld.getName().equalsIgnoreCase(defaultName)) {
                    return fld.getName();
                }
            }
            
            return defaultName;
        }
    }

