private void saveImage(Bitmap bitmap) {
        String root = Environment.getExternalStoragePublicDirectory(Environment.DIRECTORY_PICTURES).toString();
        File myDir = new File(root + &quot;/Saved Images&quot;);

        if (!myDir.exists()) {
            myDir.mkdirs();
