        String getCustomerName();
        @Mapping(&quot;orderItems[interactionCode != fully.qualified.name.InteractionCode.TERMINATE]&quot;)
        Set&lt;OrderItemDto&gt; getOrderItems();

        @EntityView(OrderItemEntity.class)
        interface OrderItemDto {
            @IdMapping
