          System.out.println(&quot;% Error: &quot; + (absError/realValue)*100);
          System.out.println();
        }
      }
      static double trapezoidRule(double a, double b, MathFunc f, double n) {
        double deltaX = (b-a)/n;
        double i = 0;
        double sum = 0.0;
        while( i++ &lt;= n ) {
          if(i == 0 || i == n) {
            sum += f.apply(a + (i*deltaX));
          } else {
            sum += 2 * f.apply(a + (i*deltaX));
          }
        }
        return (deltaX * sum) / 2.0;
