    return StreamSupport.stream(splt, false).onClose(scan::close)
        .flatMap(s -&gt; r.matcher(s)
            .results() // Stream&lt;MatchResult&gt;
            .map(mr -&gt; mr.group(1) + mr.group(4)) // errorCode + error
            .map(String::toUpperCase)
        ) // Stream&lt;String&gt;
        .collect(Collectors.joining(&quot;\n&quot;));
}
