    @Test
    public void shouldMapHashMapToFoo() {
        HashMap&lt;String, String&gt; map = new HashMap&lt;&gt;();
        map.put(&quot;a&quot;, &quot;aaaa&quot;);
        map.put(&quot;b&quot;, &quot;bbbb&quot;);

