    private static FST&lt;Object&gt; buildFst(ArrayList&lt;String&gt; keywordsList) throws IOException {
    	Builder&lt;Object&gt; builder = new Builder&lt;&gt;(FST.INPUT_TYPE.BYTE1, NoOutputs.getSingleton());
    
    	IntsRefBuilder scratchInts = new IntsRefBuilder();
    	for (String s : keywordsList) {
    		BytesRef scratchBytes = new BytesRef(s);
    		builder.add(Util.toIntsRef(scratchBytes, scratchInts), NoOutputs.getSingleton().getNoOutput());
    	}
