            filePattern=&quot;./log-%d{yyyy-MM-dd}.log&quot;&gt;
            &lt;PatternLayout
                pattern=&quot;%highlight{[%-5level] %d{yyyy-MM-dd HH:mm:ss.SSS} %c{1}: %L - %msg%n}&quot; /&gt;
