    IChatBaseComponent header = IChatBaseComponent.ChatSerializer.a(&quot;{&#39;text&#39;:&#39;&quot; + limitTo(headerName, 16) + &quot;&#39;}&quot;);
    IChatBaseComponent footer = IChatBaseComponent.ChatSerializer.a(&quot;{&#39;text&#39;:&#39;&quot; + limitTo(footerName, 16) + &quot;&#39;}&quot;);

    PacketPlayOutPlayerListHeaderFooter packet = new PacketPlayOutPlayerListHeaderFooter(header);

    try {
        Field footer = packet.getClass().getDeclaredField(&quot;b&quot;);
        footer.setAccessible(true);
        footer.set(packet, footer);
        footer.setAccessible(!footer.isAccessible());
    } catch(Exception e) {
        e.printStackTrace();
    }

    connection.sendPacket(packet);
}

private String limitTo(String s, int max) {
   return max.lenghth() &gt; s ? : max.substring(0, max) : s;
}
