Trie implementation in java. This avoids scan over the entire wordlist, so its complexity is limited to char length.


    import java.util.*;
    import java.util.stream.Stream;

    public class WordFinder {
    class TrieNode {
        boolean isEnd;
        TrieNode[] children;
        String word;

        TrieNode() {
            isEnd = false;
            children = new TrieNode[26];
        }
    }

    TrieNode root;

    public void init(String[] words) {
        root = new TrieNode();
        for (String w : words) {
            insert(w);
        }
    }

    private void insert(String w) {
        TrieNode curr = root;

        for (int i = 0; i &lt; w.length(); ++i) {
            char c = w.charAt(i);

            if (curr.children[c - &#39;a&#39;] == null)
                curr.children[c - &#39;a&#39;] = new TrieNode();

            curr = curr.children[c - &#39;a&#39;];
        }

        curr.isEnd = true;
        curr.word=w;
    }


    public String[] find(char[] chars) {

        Map&lt;Character, Integer&gt; mp = new HashMap&lt;&gt;();
        for (int i = 0; i &lt; chars.length; ++i) {
            char c = chars[i];
            mp.put(c, mp.getOrDefault(c, 0) + 1);
        }
        Set&lt;String&gt; set  = new HashSet&lt;&gt;();
        dfs(root, mp, 0,set);
        return set.toArray(new String[0]);
    }

    private void dfs(TrieNode curr, Map&lt;Character, Integer&gt; mp, int l,Set&lt;String&gt; set) {
        //base
        if (curr.isEnd) {
            set.add(curr.word);
        }

        //logic
        for (int i = 0; i &lt; 26; ++i) {
            char c = (char) (i + &#39;a&#39;);

            if (curr.children[i] != null) {
                if (mp.containsKey(c) &amp;&amp; mp.get(c) &gt; 0) {
                    mp.put(c, mp.get(c) - 1);
                    dfs(curr.children[i], mp, l + 1,set);
                    mp.put(c, mp.get(c) + 1);
                }
            }
        }
    }
    public static void main(String[] args){
        WordFinder wordFinder=new WordFinder();
        String[] words = { &quot;wood&quot;, &quot;word&quot;, &quot;words&quot; };
        char[] chars={ &#39;o&#39;, &#39;w&#39;, &#39;o&#39;, &#39;s&#39;, &#39;d&#39;, &#39;a&#39;, &#39;r&#39; };
